package com.example.ronch02.recipeapp.Model;import android.content.Intent;import android.os.Bundle;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.example.ronch02.recipeapp.MainActivity;import com.example.ronch02.recipeapp.R;import androidx.appcompat.app.AppCompatActivity;public class DetailActivity extends AppCompatActivity {    public static final String RECIPE_POSITION = "recipe_position";    TextView mRecipe;    TextView mName;    TextView mIngredients;    ImageView mImageView;    TextView mProteins;    TextView mDescription;    TextView mFavorites;    TextView mHeadline;    TextView mCalories;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_detail);        mImageView = findViewById(R.id.recipe_thumb);        Intent intent = getIntent();        mRecipe = findViewById(R.id.recip_name);        mDescription = findViewById(R.id.recip_description);        mIngredients = findViewById(R.id.recip_ingredient);        mProteins = findViewById(R.id.recip_proteins);        mName = findViewById(R.id.recip_calories);        mHeadline = findViewById(R.id.recipe_headlines);        mFavorites = findViewById(R.id.recip_favorites);        mCalories = findViewById(R.id.recip_calories);        if (intent != null) {            int position = intent.getIntExtra(RECIPE_POSITION, 0);            Recipe recipe = MainActivity.getRecipe(position);            Glide.with(this).load(recipe.getImage()).into(mImageView);            mDescription.setText(recipe.getDescription());            mProteins.setText(recipe.getProteins());            mName.setText(recipe.getName());            mRecipe.setText(recipe.getName());            mHeadline.setText(recipe.getHeadline());            mIngredients.setText(recipe.getIngredients().toString());            mFavorites.setText(recipe.getFavorites().toString());            mCalories.setText(recipe.getCalories());        }    }}