package com.example.ronch02.recipeapp.adapter;import android.content.Context;import android.content.Intent;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.RatingBar;import android.widget.TextView;import com.bumptech.glide.Glide;import com.example.ronch02.recipeapp.Model.DetailActivity;import com.example.ronch02.recipeapp.Model.Recipe;import com.example.ronch02.recipeapp.R;import java.util.List;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;public class RecipeAdapter extends RecyclerView.Adapter<RecipeAdapter.MyHolder> {    Context mContext;    List<Recipe> recipes;    RecipeOnclickListener mListener;    public interface RecipeOnclickListener {        void onClick(int position);    }    public RecipeAdapter(Context context, List<Recipe> recipes) {        mContext = context;        this.recipes = recipes;    }    @Override    public int getItemViewType(int position) {        return super.getItemViewType(position);    }    @Override    public void onBindViewHolder(@NonNull MyHolder holder, int position) {        Recipe recipe = recipes.get(position);        holder.headline.setText(recipe.getHeadline());        holder.name.setText(recipe.getName());        if (recipe.getRating() == null) {            holder.rating.setRating(0);        } else {            holder.rating.setRating(recipe.getRating().floatValue());        }        Glide.with(mContext)                       .load(recipe                        .getImage())                        .into(holder.thumbnail);    }    @Override    public int getItemCount() {        return recipes == null ? 0 : recipes.size();    }    public void setRecipes(List<Recipe> recipes) {        for (Recipe recipe : recipes) {            addRecipe(recipe);        }    }    public void addRecipe(Recipe recipe) {        recipes.add(recipe);        notifyItemInserted(recipes.size());    }    @NonNull    @Override    public MyHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from(mContext).inflate(R.layout.recipe_inflate, parent, false);        return new MyHolder(view);    }    class MyHolder extends RecyclerView.ViewHolder implements View.OnClickListener {        private ImageView thumbnail;        private TextView headline;        private RatingBar rating;        private TextView name;        private TextView proteins;        private TextView ingredient;        private TextView favorites;        public MyHolder(View itemView) {            super(itemView);            name = itemView.findViewById(R.id.recipe_name);            thumbnail = itemView.findViewById(R.id.recipe_thumbnail);            headline = itemView.findViewById(R.id.recipe_headline);            rating = itemView.findViewById(R.id.recipe_rating);            proteins = itemView.findViewById(R.id.recip_proteins);            ingredient = itemView.findViewById(R.id.recip_ingredient);            favorites = itemView.findViewById(R.id.recip_favorite);            itemView.setOnClickListener(this);        }        @Override        public void onClick(View view) {            Intent intent = new Intent(mContext, DetailActivity.class);            intent.putExtra(DetailActivity.RECIPE_POSITION, getAdapterPosition());            mContext.startActivity(intent);        }    }}